/* eslint-disable */
import React, {useState} from 'react';
import logo from './logo.svg';
import './App.css';

function App() {
  let [글제목, 글제목변경] = useState(['남자 코트 추천', '강남 우동 맛집', '파이썬독학']);
  let [따봉, 따봉변경] = useState(0);

  let [modal, modal변경] = useState(false);

  function forUi(){

    var arrayEx = [];
    for(var i = 0; i < 3; i++){
      arrayEx.push(<div>안녕</div>);
    }

    return arrayEx;
  }

  let posts = '강남 고기 맛집';

  function titleChange(){
    let newArray = [...글제목];
    newArray[0] = '여자 코트 추천';
    글제목변경( newArray );
  }

  return (
    <div className="App">
      <div className="black-nav">
        <div style={ { color : 'blue', fontSize : '30px' } } >개발 Blog</div>
      </div>
      <button onClick={ titleChange }>버튼</button>

      { forUi() }

      {
        글제목.map(function(a){
          return (
            <div className="list">
              <h3 onClick={ ()=>{ modal === false ? modal변경(true) : modal변경(false) }}> { a } <span onClick={ ()=>{ 따봉변경(따봉 + 1) } }>👍</span> { 따봉 } </h3>
              <p>2월 17일 발행</p>
              <hr />
            </div>
          )
        })
      }


      {
        modal === true 
        ? <Modal /> 
        : null
      }


    </div>
  );
}
// component 이름의 첫글자를 대문자로 시작해야한다.
// return 안에 무조건 div 하나를 감싸고 html코딩이 들어가야한다.
// 만약 div를 쓰기 싫다면 <> {html코딩} </> 이런식으로 감싸주어도 괜찮다.
function Modal() {
  return(
    <div className="modal">
      <h2>제목</h2>
      <p>날짜</p>
      <p>상세내용</p>
    </div>
  )
}

export default App;
